mapper.update:
UPDATE legao_relation
SET
signInStatus = #{relationPO.signInStatus},
modifyPerson = #{relationPO.modifyPerson}
WHERE id = #{caption}

mapper.select:
SELECT legao_relation.id, legao_relation.scheduleID, legao_relation.studentID, legao_relation.signInStatus, legao_relation.lastModifyTime, legao_relation.modifyPerson FROM legao_relation
and studentID = #{studentID}
and lastModifyTime = #{lastModifyTimeStr}

SELECT count(1)
FROM legao_relation
WHERE scheduleID = #{id}

SELECT studentID,signInStatus
FROM legao_relation
WHERE scheduleID = #{id}
